	@Override
	public boolean nameLastCheck(User user) {
		if (user.getName().length()<2||user.getSurname().length()<2) {
			System.out.println("Ad Soyad en az iki karakter olmalıdır.");
			return false;
		}
		
		return true;
	}

	@Override
	public boolean passwordCheck(User user) {
		if (user.getPassword().length()<6) {
			System.out.println("parola en az 6 haneden oluşmalıdır.");
			return false;
		}
		return true;
	}

